import React, { useState } from 'react';
import { Route, BrowserRouter as Router, Switch } from 'react-router-dom';
import { useHistory } from 'react-router-dom';
import './App.css';
import { PostType } from './assets/posts';
import { CreatePost } from './components/CreatePost/CreatePost';
import { Feed } from './components/GalleryFeed/Feed/Feed';
import Navbar from './components/Navbar/Navbar';
import ThemeProvider from './hoc/ThemeProvider';

function App() {
  // sostojba na kreirani postovi
  const [createdPosts, setCreatedPosts] = useState<PostType[]>([]);

  const onNewPost = (newPost: PostType) => {
    setCreatedPosts([...createdPosts, newPost]);
  };

  return (
    <Router>
      <ThemeProvider>
        <Navbar />
        <Switch>
          <Route exact path="/">
            <Feed createdPosts={createdPosts}></Feed>
          </Route>
          <Route exact path="/create">
            <CreatePost onNewCreatedPost={onNewPost}></CreatePost>
          </Route>
        </Switch>
      </ThemeProvider>
    </Router>
  );
}

export default App;
