import { createContext } from 'react';

interface DefaultContextValues {
  darkMode: boolean;
  toggleTheme: () => void;
  theme: 'light' | 'dark';
}


// ova  obicna komponenta
export const DarkModeContext = createContext<DefaultContextValues>({
  darkMode: true,
  toggleTheme: () => {},
  theme: 'light'
});
